import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import { useUser, useSupabaseClient } from "@supabase/auth-helpers-react";

const customStyles = {};

const CreatePhotoModal = ({ setImages }) => {
  const user = useUser();
  const supabase = useSupabaseClient();
  const [modalIsOpen, setIsOpen] = useState(false);
  const [file, setFile] = useState("");
  const [name, setName] = useState("");
  const [form, setForm] = useState(true);

  const openModal = () => {
    setIsOpen(true);
  };

  function closeModal() {
    setIsOpen(false);
    setName("");
    setForm(true);
    setFile("");
  }

  const handleChange = (event) => {
    setFile(event);
  };

  const handleChangeName = (event) => {
    setName(event.target.value);
    if (name.length > 0 && file) {
      setForm(false);
    } else {
      setForm(true);
    }
  };

  const handleClick = () => {
    uploadImage(file);
    closeModal();
  };

  useEffect(() => {
    if (user) {
      getImages();
    }
  }, [user]);

  async function getImages() {
    const { data, error } = await supabase.storage
      .from("images")
      .list(user?.id + "/", {
        limit: 100,
        offset: 0,
        sortBy: { column: "created_at", order: "desc" },
      });

    if (data !== null) {
      setImages(data);
    } else {
      console.log(error);
    }
  }

  async function uploadImage(ev) {
    let file = ev.target.files[0];

    const { data, error } = await supabase.storage
      .from("images")
      .upload(user.id + "/" + name, file);

    if (data) {
      getImages();
    } else {
      console.log(error);
    }
  }

  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        paddingBottom: "32px",
      }}
    >
      <button className="btn default" onClick={openModal}>
        Share a memory
      </button>
      <Modal
        className="modal-pop"
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <div
          style={{
            display: "flex",
            width: "100%",
            justifyContent: "space-between",
            marginBottom: "16px",
          }}
        >
          <h2 className="first-header-text" style={{ margin: 0 }}>
            Share a memory
          </h2>
          <button onClick={closeModal} className="close">
            &times;
          </button>
        </div>

        <form style={{ display: "flex", flexDirection: "column", gap: "8px" }}>
          <input type="file" onChange={(e) => handleChange(e)} />
          <p className="helper-text">
            <span>Only JPEG images less than 50MB in size</span>
          </p>
          <label for="textInput">Describe the special moment</label>
          <input
            onChange={(e) => handleChangeName(e)}
            type="text"
            value={name}
            className="input-field"
            id="textInput"
          ></input>
          <p className="helper-text">
            <span>Sadly no unique characters and emojis 😞</span>
          </p>
          <button
            className="btn default upload"
            disabled={form}
            onClick={() => {
              handleClick();
            }}
          >
            Upload photo
          </button>
        </form>
      </Modal>
    </div>
  );
};

export default CreatePhotoModal;
